public class TestOperator05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String a = "3";
		int b =4;
		int c = 5;
		char d = 'a';
		System.out.println(a + b + c);
		System.out.println(b + c + a);
		System.out.println(d + 4); //94 + 4 = 101
	}

}
