package three.nine;
/**
 * 位运算符
 * @author 73946
 *
 */
public class TestOperator04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 3;
		int b = 4;
		System.out.println(a & b);
		System.out.println(a | b);
		System.out.println(a ^ b);
		System.out.println(~a);
		
		int c = 3<<2;
		System.out.println(c);
		System.out.println(12 >> 1);
	}

}
